"use strict";(self.webpackChunkdocs=self.webpackChunkdocs||[]).push([[477],{10:function(e){e.exports=JSON.parse('{"blogPosts":[{"id":"\u804a\u7ee9\u6548\u662f\u804a\u4ec0\u4e48","metadata":{"permalink":"/blog/\u804a\u7ee9\u6548\u662f\u804a\u4ec0\u4e48","editUrl":"https://github.com/7213/my-favorite-code-segment/tree/main/docs/blog/2022-11-05.md","source":"@site/blog/2022-11-05.md","title":"\u4e00\u4e2a\u4e00\u7ebf\u7ba1\u7406\u8005\u804a\u7ee9\u6548\uff0c\u662f\u804a\u4ec0\u4e48\uff1f","description":"\u629b\u5f00\u7ba1\u7406\u8005\u57f9\u8bad\u54ea\u4e9b\uff0c\u4ece\u6211\u4e00\u4e2a\u4e00\u7ebf\u6280\u672f TL \u7684\u89c6\u89d2\u4e0a\u770b\uff0c\u804a\u4e00\u804a\u8fd9\u4e9b\u5e74\u5bf9\u4e8e\\"\u7ee9\u6548\u9762\u8c08\\"\u7684\u4e00\u4e9b\u611f\u53d7\uff1a\u5f53\u6211\u53bb\u804a\u7ee9\u6548\u65f6\uff0c\u804a\u7684\u662f\u4ec0\u4e48\uff1f","date":"2022-11-05T00:00:00.000Z","formattedDate":"November 5, 2022","tags":[],"readingTime":4.075,"truncated":false,"authors":[],"nextItem":{"title":"\u5f53\u4e00\u7ebf\u6280\u672f\u7ba1\u7406\u8fd9\u4e00\u5e74","permalink":"/blog/\u5f53\u4e00\u7ebf\u6280\u672f\u7ba1\u7406\u8fd9\u4e00\u5e74"}},"content":"\u629b\u5f00\u7ba1\u7406\u8005\u57f9\u8bad\u54ea\u4e9b\uff0c\u4ece\u6211\u4e00\u4e2a\u4e00\u7ebf\u6280\u672f TL \u7684\u89c6\u89d2\u4e0a\u770b\uff0c\u804a\u4e00\u804a\u8fd9\u4e9b\u5e74\u5bf9\u4e8e\\"\u7ee9\u6548\u9762\u8c08\\"\u7684\u4e00\u4e9b\u611f\u53d7\uff1a\u5f53\u6211\u53bb\u804a\u7ee9\u6548\u65f6\uff0c\u804a\u7684\u662f\u4ec0\u4e48\uff1f\\n\\n\u9996\u5148\\"\u7ee9\u6548\u9762\u8c08\\"\u662f\u5de5\u4f5c\u8fc7\u7a0b\u7d2f\u8ba1\u5230\u4e00\u4e2a\u8282\u70b9\u65f6\u540e\u4e00\u6b21\u7684\\"\u7ed3\u679c\u4f20\u8fbe\\"\uff0c\\"\u7ee9\u6548\u9762\u804a\\"\u5e94\u8be5\u662f\u8d2f\u7a7f\u5728\u5de5\u4f5c\u788e\u7247\u59cb\u7ec8\u7684\uff0c\u5982\u679c\u6ca1\u6709\\"\u8fc7\u7a0b\u6c9f\u901a\\"\uff0c\u90a3\u4e48\\"\u7ee9\u6548\u9762\u804a\\"\u6ca1\u6709\u4efb\u4f55\u610f\u4e49\uff0c\u4e5f\u7ec8\u5c06\u4f1a\u5728\u67d0\u4e00\u65f6\u95f4\u70b9\u67d0\u4e00\u4eba\u4e0a\u70b8\u51fa\u7ba1\u7406\u4e8b\u6545\u3002\\n\\n\u9996\u5148\uff0c\u5982\u4f55\u907f\u514d\u4e00\u6b21\u574f\u7684\u7ee9\u6548\u6c9f\u901a \uff1f\u4ece\u51e0\u5e74\u7684\u53cd\u5411\u6848\u4f8b\u53bb\u770b\uff0c\u574f\u7684\u7ee9\u6548\u6c9f\u901a\u5f80\u5f80\u90fd\u4f1a\u6709\u4ee5\u4e0b\u51e0\u4e2a\u7279\u70b9\uff1a\\n\\n- \u5bf9\u4e8e\u9884\u671f\u9ad8\u7684\u540c\u5b66\uff1a\u7ba1\u7406\u8005\\"\u4ee5\u907f\u514d\u70ed\u5e16\\"\u4e3a\u51fa\u53d1\u70b9\u53bb\u6c9f\u901a\uff0c\u901a\u8fc7\u76d8\u70b9\u8bc1\u636e\u3001\u5168\u9762\u5426\u5b9a\u7684\u6c9f\u901a\u65b9\u5f0f\u8ba9\u540c\u5b66\\"\u63a5\u53d7\\"\u6700\u7ec8\u7ed3\u679c\\n- \u5bf9\u4e8e\u597d\u7ee9\u6548\u7684\u540c\u5b66\uff1a\u873b\u8713\u70b9\u6c34\u822c\u7684\u6c9f\u901a\\n- \u5bf9\u4e8e\u4e00\u822c\u7684\u540c\u5b66\uff1a\u5728\u672a\u6df1\u5165\u6316\u6398\u5bf9\u65b9\u5185\u9a71\u7684\u65f6\u5019\uff0c\u901a\u8fc7\\"\u7c97\u66b4\\"\u7684\u624b\u6bb5\u8fdb\u884c\u6fc0\u52b1\uff08\u6bd4\u5982\u4e1a\u7ee9\u8fbe\u6210\u540e\u7684\u8bb8\u8bfa\u7b49\uff09\\n\\n\\n\u5176\u5b9e\u4ee5\u4e0a\u90fd\u6307\u5411\u4e00\u70b9\uff0c\u7ba1\u7406\u8005\u66f4\u591a\u662f\u4ee5\u6ee1\u8db3\u81ea\u5df1\u67d0\u79cd\u76ee\u4e0a\uff08\u5411\u4e0a\uff09\u51fa\u53d1\uff0c\u628a\u540c\u5b66\u5f53\u6210\u8d44\u6e90\uff0c\u5728\u6700\u4f53\u73b0\u4eba\u6027\u7684\u4fee\u7f57\u573a\uff0c\u5374\u5ffd\u7565\u4e86\u5bf9\u65b9\u7684\u60c5\u611f\u9700\u8981\uff0c\u628a\u590d\u6742\u7684\u4e8b\u60c5\u7b80\u5355\u5316\u3002\\n   \\n\u90a3\u4e48\uff0c\u4ec0\u4e48\u662f\u4e00\u6b21\u6709\u6548\u7684\u7ee9\u6548\u6c9f\u901a\uff1f\u4ece\u516c\u53f8\u7684\u89d2\u5ea6\uff0c\u4efb\u4f55\u7684\u52a8\u4f5c\u90fd\u662f\u4e3a\u4e86\u4fc3\u8fdb\u7ec4\u7ec7\u7684\u8fdb\u5316\uff0c\\"\u7ee9\u6548\u6c9f\u901a\\"\u4e5f\u4e0d\u4f8b\u5916\u3002\\n\\n\u6211\u7406\u89e3\u613f\u666f\u3001\u6fc0\u52b1\u5728\u5e73\u65f6 TL \u7684\u4ee5\u8eab\u4f5c\u5219\u4e2d\u53bb\u4f20\u8fbe\u51fa\u6765\uff0c\\"\u597d\\"\u4e0e\\"\u574f\\"\u7684\u7ee9\u6548\u573a\u5b50\u662f\u4e3a\u4e86\u627e\u5230\u7ec4\u7ec7\u73b0\u5728\u3001\u672a\u6765\u9700\u8981\u7684\u4eba\uff0c\u800c\u975e\u901a\u8fc7\u7ee9\u6548\u671f\u671b\u53bb\u6539\u53d8\u4e00\u4e2a\u4eba\u3002    \\n\\n\\n- \u4e1a\u52a1\u5931\u610f\u65f6\uff1a\u7f3a\u4e4f\u6709\u6548\u7684\u5206\u914d\u8d44\u6e90\u65f6\uff0c\u8fc7\u5f80\u957f\u671f\\"\u597d\u7ee9\u6548\\"\u7684\u540c\u5b66\u662f\u5426\u8fd8\u80fd\u8ba4\u540c\u7ec4\u7ec7\u613f\u666f\uff0c\u540c\u65f6\u601d\u8003\u662f\u5426\u662f\u53ea\u662f\u987a\u98ce\u5c40\uff0c\u4e8b\u6210\u4eba\u7684\u7ed3\u679c\u3002\u672a\u6765\u9700\u8981\u4ec0\u4e48\u6837\u7684\u7834\u5c40\u8005\uff0c\u8981\u4f20\u8fbe\u4e0b\u4e00\u6b21\u8981\u4ec0\u4e48\u3002\\n- \u4e1a\u52a1\u987a\u98ce\u65f6\uff1a\u662f\u5426\u4fdd\u6301\u7ec4\u7ec7\u7684\u5e73\u8861\uff0c\u5728\u4e0d\u6539\u53d8\u5927\u5c40\u9762\u7684\u540c\u65f6\u53bb\u5bfb\u627e\u5e73\u8861\uff0c\u8ba9\u540c\u5b66\u6ee1\u610f\u548c\u4e1a\u52a1\u5371\u673a\u610f\u8bc6\u5171\u5b58\u3002\\n\\n\u6700\u540e\u4e5f\u8bf4\u4e00\u8bf4\u6211\u5e76\u4e0d\u8ba4\u540c\u7684\u7ba1\u7406\u52a8\u4f5c \uff0c\u901a\u8fc7\u5728\u8fc7\u7a0b\u4e2d\u4e0d\u505c\u7684 \\"\u964d\u9884\u671f\\" \u65b9\u5f0f\uff0c\u8ba9\u6700\u540e\u62ff\u5230\u597d\u7ed3\u679c\u7684\u540c\u5b66\\"\u60ca\u559c\\"\uff0c\u8ba9\u574f\u7ed3\u679c\u7684\u540c\u5b66\u4e5f\\"\u987a\u5e94\u63a5\u53d7\\"\u3002  \\n\\n\u4e5f\u8bb8\u8fd9\u4e2a Leader \u77ed\u65f6\u95f4\u770b\u8d77\u6765\u7ba1\u7406\u624b\u6bb5\u5f88\u5f3a\uff0c\u4f46\u957f\u6b64\u4ee5\u5f80\uff0c\u8fd9\u4e2a\u7ec4\u7ec7\u6700\u540e\u66f4\u591a\u7684\u662f\u4e00\u4e9b\\"\u987a\u6c11\\"\uff0c\\"\u542c\u8bdd\u7684\u806a\u660e\u8005\\"\u66f4\u5bb9\u6613\u62ff\u5230\u597d\u7ed3\u679c\uff0c\u7ec4\u7ec7\u5c06\u5931\u53bb\u8be5\u6709\u7684\u5f39\u6027\u548c\u521b\u9020\u529b\uff0c\u8d70\u5411\u5e73\u5eb8\u3002\\n\\n\u540c\u65f6\u4f5c\u4e3a\u4e00\u7ebf\u7ba1\u7406\u8005\u81ea\u5df1\u7684\u4e00\u4e9b\u53cd\u601d\uff1a\\n\\n- 1.\u4e5f\u8bb8\u5df2\u7ecf\u6709\u5f88\u591a\u7684\\"\u7ba1\u7406\u8fc7\u7a0b\\"\uff0c\u4f46\u6709\u6ca1\u6709\u53bb\u4f53\u4f1a\u540c\u5b66\u7684\\"\u6c42\u6551\\"\u4fe1\u53f7\uff0c\u8fc7\u7a0b\u4e2d\u662f\u5426\u6362\u4f4d\u601d\u8003\uff0c\u662f\u5426\u4f60\u771f\u7684\u7ed9\u8fc7\u8d44\u6e90\u6216\u5e2e\u52a9\\n- 2.\u662f\u5426\u4ece\u8ba9\\"\u7ec4\u7ec7\u66f4\u597d\\"\u7684\u89d2\u5ea6\u53bb\u51b3\u5b9a\uff0c\u8fd8\u662f\u4f60\u597d\u6211\u597d\u5927\u5bb6\u597d\uff0c\u5f53\u7136\u8fd9\u91cc\u662f\u5426\u6709\u627f\u62c5\u7ed3\u679c\u7684\u52c7\u6c14\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u4e5f\u51b3\u5b9a\u4e8e TL \u7684 TL \u662f\u4e0d\u662f\u4e5f\u6709\u8fd9\u5c42\u515c\u5e95\u7684\u52c7\u6c14"},{"id":"\u5f53\u4e00\u7ebf\u6280\u672f\u7ba1\u7406\u8fd9\u4e00\u5e74","metadata":{"permalink":"/blog/\u5f53\u4e00\u7ebf\u6280\u672f\u7ba1\u7406\u8fd9\u4e00\u5e74","editUrl":"https://github.com/7213/my-favorite-code-segment/tree/main/docs/blog/2021-11-7.md","source":"@site/blog/2021-11-7.md","title":"\u5f53\u4e00\u7ebf\u6280\u672f\u7ba1\u7406\u8fd9\u4e00\u5e74","description":"\u6210\u5c31\u4ed6\u4eba","date":"2021-11-07T00:00:00.000Z","formattedDate":"November 7, 2021","tags":[],"readingTime":0.19,"truncated":false,"authors":[],"prevItem":{"title":"\u4e00\u4e2a\u4e00\u7ebf\u7ba1\u7406\u8005\u804a\u7ee9\u6548\uff0c\u662f\u804a\u4ec0\u4e48\uff1f","permalink":"/blog/\u804a\u7ee9\u6548\u662f\u804a\u4ec0\u4e48"}},"content":"#### \u6210\u5c31\u4ed6\u4eba\\n\\n#### \u6210\u5c31\u4ed6\u4eba\u7684\u524d\u63d0\uff0c\u662f\u80fd\u548c\u4ed6\u4eba\u5171\u60c5\\n\\n#### \u80fd\u63a5\u53d7\u81ea\u5df1\u7684\u5e73\u5eb8\uff0c\u8981\u5b66\u4f1a\u503e\u542c\\n\\n#### \u907f\u514d\u7406\u60f3\u4e3b\u4e49"}]}')}}]);